pragma solidity ^0.4.24;

import "./OrganisationsV1.sol";

contract OrganisationsRelay {
    address public owner;
    uint public createdAt;
    OrganisationsV1 public organisationsContract;

    modifier restrictToOwner {
        if (tx.origin == owner) _;
    }

    constructor(OrganisationsV1 oContract) public {
        organisationsContract = oContract;
        owner = tx.origin;
        createdAt = now;
    }

    function addOrganisation(string id, address organisationOwner) external restrictToOwner {
        organisationsContract.addOrganisation(id, organisationOwner);
    }

    function removeOrganisation(string id) external restrictToOwner {
        organisationsContract.removeOrganisation(id);
    }

    function renameOrganisation(string oldId, string newId) external restrictToOwner {
        organisationsContract.renameOrganisation(oldId, newId);
    }

    function changeOrganisationOwner(string id, address organisationOwner) external restrictToOwner {
        organisationsContract.changeOrganisationOwner(id, organisationOwner);
    }

    function getOrganisation(string id) external view returns (address, address[], string, uint) {
        return organisationsContract.getOrganisation(id);
    }

    function getOrganisationByHash(bytes32 hash) external view returns (address, address[], string, uint) {
        return organisationsContract.getOrganisationByHash(hash);
    }

    function getOrganisationByIndex(uint index) external view returns (address, address[], string, uint) {
        return organisationsContract.getOrganisationByIndex(index);
    }

    function getOrganisationCount() external view returns (uint256) {
        return organisationsContract.getOrganisationCount();
    }

    function setMembers(string id, address[] members) external restrictToOwner {
        organisationsContract.setMembers(id, members);
    }
}
